<?php

namespace VicoDIn\Inc\Compatible\Viwec;

use VicoDIn\Inc\Data;

defined( 'ABSPATH' ) || exit;

class VIWEC_Template {
    
    static $instance = null;
    
    public static function instance() {
        if ( null == self::$instance ) {
            self::$instance = new self();
        }
        
        return self::$instance;
    }
    
    public function __construct() {
        if ( is_plugin_active( 'email-template-customizer-for-woo/email-template-customizer-for-woo.php' )
             || is_plugin_active( 'woocommerce-email-template-customizer/woocommerce-email-template-customizer.php' ) ) {
            add_filter( 'viwec_register_email_type', array( $this, 'register_email_type' ) );
            add_filter( 'viwec_sample_subjects', array( $this, 'register_email_sample_subject' ) );
            add_filter( 'viwec_sample_templates', array( $this, 'register_email_sample_template' ) );
            add_filter( 'viwec_live_edit_shortcodes', array( $this, 'register_render_preview_shortcode' ) );
            add_filter( 'viwec_register_preview_shortcode', array( $this, 'register_render_preview_shortcode' ) );
            add_filter( 'viwec_register_replace_shortcode', array( $this, 'register_replace_shortcode' ), 10, 2 );
            add_action( 'viwec_render_content', array( $this, 'depart_render_content' ), 20, 4 );
            add_action( 'woocommerce_email_order_meta', array( $this, 'add_installment_plan_to_email') );
            $this->init_default_templates();
        }
    }
    
    public function init_default_templates() {
        global $wpdb;
        $check_exist = get_posts( [ 'post_type' => 'viwec_template', 'numberposts' => 1 ] );
        
        if ( empty( $check_exist ) ) {
            return;
        }
        
        $meta_key          = 'viwec_settings_type';
        $exist_email_types = array();
        
        $results = $wpdb->get_results( $wpdb->prepare( "SELECT post_id, meta_value FROM $wpdb->postmeta WHERE meta_key = %s", $meta_key ) ); // phpcs:ignore WordPress.DB.DirectDatabaseQuery
        
        foreach ( $results as $row ) {
            $exist_email_types[] = $row->meta_value;
        }
        
        $site_title       = get_option( 'blogname' );
        $sample_subjects  = $this->register_email_sample_subject();
        $sample_templates = $this->register_email_sample_template();
        
        foreach ( $sample_templates as $type => $template ) {
            if ( ! in_array( $type, $exist_email_types ) ) {
                $args     = [
                    'post_title'  => str_replace( '{site_title}', $site_title, $sample_subjects[ $type ] ),
                    'post_status' => 'publish',
                    'post_type'   => 'viwec_template',
                ];
                $post_id  = @wp_insert_post( $args );
                $template = $template['basic']['data'];
                $template = str_replace( '\\', '\\\\', $template );
                update_post_meta( $post_id, 'viwec_settings_type', $type );
                update_post_meta( $post_id, 'viwec_email_structure', $template );
            }
        }
    }
    
    public function register_email_type( $types ) {
        $email_types = array(
            'depart_deposit_paid'       => esc_html__( 'DEPART - Deposit payment received', 'depart-deposit-and-part-payment-for-woocommerce' ),
            'depart_partial_paid'       => esc_html__( 'DEPART - Partial payment received', 'depart-deposit-and-part-payment-for-woocommerce' ),
            'depart_full_payment'       => esc_html__( 'DEPART - Full payment received', 'depart-deposit-and-part-payment-for-woocommerce' ),
            'depart_payment_reminder'   => esc_html__( 'DEPART - Payment reminder', 'depart-deposit-and-part-payment-for-woocommerce' ),
            'depart_admin_partial_paid' => esc_html__( 'DEPART - Partial paid', 'depart-deposit-and-part-payment-for-woocommerce' ),
            'depart_admin_full_payment' => esc_html__( 'DEPART - Full payment', 'depart-deposit-and-part-payment-for-woocommerce' ),
        );
        
        foreach ( $email_types as $id => $name ) {
            $types [ $id ] = array(
                'name'            => $name,
                'accept_elements' => array(
                    'html/order_detail',
                    'html/order_subtotal',
                    'html/order_total',
                    'html/shipping_method',
                    'html/payment_method',
                    'html/order_note',
                    'html/billing_address',
                    'html/shipping_address',
                    'html/wc_hook',
                    'html/coupon',
                ),
            );
        }
        
        return $types;
    }
    
    public function register_email_sample_subject( $subjects = array() ) {
        $subjects['depart_partial_paid']       = esc_html__( '[{site_title}]: Partial payment [#{wdp_suborder_number}] has been received!', 'depart-deposit-and-part-payment-for-woocommerce' );
        $subjects['depart_payment_reminder']   = esc_html__( '[{site_title}]: Payment reminder for order #{wdp_parent_order_number}!', 'depart-deposit-and-part-payment-for-woocommerce' );
        $subjects['depart_deposit_paid']       = esc_html__( '[{site_title}]: Your deposit order has been received!', 'depart-deposit-and-part-payment-for-woocommerce' );
        $subjects['depart_full_payment']       = esc_html__( '[{site_title}]: Your instalment plan has been completed!', 'depart-deposit-and-part-payment-for-woocommerce' );
        $subjects['depart_admin_partial_paid'] = esc_html__( '[{site_title}]: Partial payment for [#{order_number}]', 'depart-deposit-and-part-payment-for-woocommerce' );
        $subjects['depart_admin_full_payment'] = esc_html__( '[{site_title}]: Installment plan completed for #{wdp_parent_order_number}', 'depart-deposit-and-part-payment-for-woocommerce' );
        
        return $subjects;
    }
    
