    public function register_render_preview_shortcode( $sc ) {
        $date_format        = get_option( 'date_format' );
        $deposit_shortcodes = array(
            '{wdp_parent_order_number}' => '2157',
            '{wdp_suborder_number}'     => '2157-1',
            '{wdp_payment_due_date}'    => date_i18n( $date_format, strtotime( '+30 days' ) ),
            '{wdp_deposit_amount}'      => wc_price( 20 ),
            '{wdp_partial_amount}'      => wc_price( 20 ),
            '{wdp_paid_amount}'         => wc_price( 25 ),
            '{wdp_remaining_amount}'    => wc_price( 20 ),
            '{wdp_fee_total}'           => wc_price( 0 ),
            '{wdp_payment_link}'        => '',
            '{wdp_order_detail_link}'   => '',
            '{wdp_order_billing_name}'  => 'Villatheme',
        );
        
        if ( is_plugin_active( 'woocommerce-email-template-customizer/woocommerce-email-template-customizer.php' ) ) {
            $price_0      = wc_price( 0 );
            $price_5      = wc_price( 5 );
            $price_10     = wc_price( 10 );
            $price_50     = wc_price( 50 );
            $price_55     = wc_price( 55 );
            $order_number = 1234;
            $order_id     = 1234;
            
            $shortcode_for_order = [
                '{customer_name}'         => esc_html__( 'John Doe', 'viwec-email-template-customizer' ),
                '{first_name}'            => esc_html__( 'John', 'viwec-email-template-customizer' ),
                '{last_name}'             => esc_html__( 'Doe', 'viwec-email-template-customizer' ),
                '{customer_email}'        => 'johndoe@domain.com',
                '{customer_note}'         => esc_html__( 'Customer note', 'viwec-email-template-customizer' ),
                '{customer_phone_number}' => '0123-456-789',
                '{order_date}'            => date_i18n( $date_format, current_time( 'U' ) ),
                '{order_discount}'        => $price_5,
                '{order_fully_refund}'    => $price_0,
                '{order_note}'            => esc_html__( 'Order note', 'viwec-email-template-customizer' ),
                '{order_id}'              => $order_id,
                '{order_number}'          => $order_number,
                '{view_order_url}'        => $order_number,
                '{order_partial_refund}'  => $price_0,
                '{order_received_url}'    => wc_get_endpoint_url( 'order-received', $order_number, wc_get_checkout_url() ),
                '{order_shipping}'        => $price_10,
                '{order_subtotal}'        => $price_50,
                '{order_total}'           => $price_55,
                '{order_tax}'             => $price_5,
                '{payment_method}'        => esc_html__( 'Paypal', 'viwec-email-template-customizer' ),
                '{payment_url}'           => wc_get_endpoint_url( 'order-pay', $order_number, wc_get_checkout_url() ) . '?pay_for_order=true&key=wc_order_6D6P8tQ0N',
                '{shipping_method}'       => esc_html__( 'Flat rate', 'viwec-email-template-customizer' ),
            ];
            
            $deposit_shortcodes = array_merge( $shortcode_for_order, $deposit_shortcodes );
            $sc                 = array_merge( $sc, array(
                'depart_deposit_paid'       => $deposit_shortcodes,
                'depart_partial_paid'       => $deposit_shortcodes,
                'depart_full_payment'       => $deposit_shortcodes,
                'depart_payment_reminder'   => $deposit_shortcodes,
                'depart_admin_partial_paid' => $deposit_shortcodes,
                'depart_admin_full_payment' => $deposit_shortcodes,
            ) );
        } elseif ( is_plugin_active( 'email-template-customizer-for-woo/email-template-customizer-for-woo.php' ) ) {
            $sc['depart_admin_partial_paid'] = $deposit_shortcodes;
        }
        
        return $sc;
    }
    
    public function register_replace_shortcode( $args, $object ) {
        if ( is_a( $object, 'WC_Order' ) ) {
            if ( isset( $object->suborder ) && is_a( $object->suborder, 'WC_Order' ) ) {
                $args[] = array(
                    '{wdp_suborder_number}'       => $object->suborder->get_order_number(),
                    '{wdp_parent_order_number}'   => $object->get_order_number(),
                    '{wdp_payment_due_date}'      => date_i18n( wc_date_format(), $object->suborder->get_meta( '_depart_partial_payment_date' ) ),
                    '{wdp_payment_link}'          => esc_url( $object->get_checkout_payment_url() ),
                    '{wdp_deposit_amount}'        => wc_price( $object->get_meta( '_depart_deposit_amount' ) ),
                    '{wdp_future_payment_amount}' => wc_price( $object->get_meta( '_depart_future_payment' ) ),
                    '{wdp_partial_amount}'        => wc_price( $object->suborder->get_total() ),
                    '{wdp_order_detail_link}'     => esc_url( $object->get_view_order_url() ),
                    '{wdp_edit_order_link}'       => esc_url( $object->get_edit_order_url() ),
                    '{wdp_paid_amount}'           => $remaining = floatval( $object->get_meta( '_depart_paid_amount' ) ),
                    '{wdp_remaining_amount}'      => $object->get_total() - $remaining,
                    '{wdp_fee_total}'             => floatval( $object->get_meta( '_depart_fee_total' ) ),
                    '{wdp_order_billing_name}'    => $object->get_formatted_billing_full_name(),
                );
            }
        }
        
        return $args;
    }
    
    public function depart_render_content( $type, $props, $email_render, $arr_style = '' ) {
        $func = 'render_' . $type;
        if ( method_exists( $this, $func ) ) {
            $this->$func( $props, $arr_style, $email_render );
        }
    }
    
    public function render_html_order_total( $props, $arr_style, $email_render ) {
        $html = '';
        if ( $email_render->order ) {
            if ( $email_render->order->get_meta( '_depart_is_deposit_order' ) ) {
                $left_style  = isset( $props['childStyle']['.viwec-td-left'] ) ? $email_render->parse_styles( $props['childStyle']['.viwec-td-left'] ) : 'text-align:left;';
                $right_style = isset( $props['childStyle']['.viwec-td-right'] ) ? $email_render->parse_styles( $props['childStyle']['.viwec-td-right'] ) : 'text-align:right; width:40%;';
                $el_style    = isset( $props['childStyle']['.viwec-order-total-style'] ) ? $email_render->parse_styles( $props['childStyle']['.viwec-order-total-style'] ) : '';
                $font_family = $props['style']['font-family'] ?? 'inherit';
                
                $total         = floatval( $email_render->order->get_meta( '_depart_total' ) );
                $fee_total     = floatval( $email_render->order->get_meta( '_depart_fee_total' ) );
                $paid_amount   = floatval( $email_render->order->get_meta( '_depart_paid_amount' ) );
                
                /* Check last suborder was paid or not */
                $suborder = null;
                if (property_exists($email_render, 'order') && property_exists($email_render->order, 'suborder')) {
                    $suborder = $email_render->order->suborder;
                }
                if ( $suborder && $suborder->is_paid() ) {
                    $paid_amount += $suborder->get_total();
                }
                $unpaid_amount = $total + $fee_total - $paid_amount;
                
                $currency_args = [
                    'currency' => $email_render->order->get_currency(),
                    'decimals' => wc_get_price_decimals(),
                ];
                
                $fees_text      = __( 'Fees', 'depart-deposit-and-part-payment-for-woocommerce' );
                $fee_total      = wc_price( $fee_total, $currency_args );
                $paid_text      = __( 'Paid', 'depart-deposit-and-part-payment-for-woocommerce' );
                $paid_amount    = wc_price( $paid_amount, $currency_args );
                $remaining_text = __( 'Remaining', 'depart-deposit-and-part-payment-for-woocommerce' );
                $unpaid_amount  = wc_price( $unpaid_amount, $currency_args );
                
                $p_style = $email_render->get_p_inherit_style( $props );
                
                $p_style_label = apply_filters( 'order_total_label_p_style', $p_style );
                $p_style_value = apply_filters( 'order_total_value_p_style', $p_style );
                
                $html    .= "<tr><td valign='top' class='viwec-mobile-50' style='font-family: {$font_family};{$el_style}{$left_style}{$p_style}'><p style='{$p_style_label}'>{$fees_text}</p></td>";
                $html    .= "<td valign='top' class='viwec-mobile-50' style='font-family: {$font_family};{$el_style}{$right_style}'><p style='{$p_style_value}'>{$fee_total}</p></td></tr>";
                $html    .= "<tr><td valign='top' class='viwec-mobile-50' style='font-family: {$font_family};{$el_style}{$left_style}'><p style='{$p_style_value}'>{$paid_text}</p></td>";
                $html    .= "<td valign='top' class='viwec-mobile-50' style='font-family: {$font_family};{$el_style}{$right_style}'><p style='{$p_style_value}'>{$paid_amount}</p></td>";
                $html    .= "</tr>";
                $html    .= "<tr><td valign='top' class='viwec-mobile-50' style='font-family: {$font_family};{$el_style}{$left_style}'><p style='{$p_style_value}'>{$remaining_text}</p></td>";
                $html    .= "<td valign='top' class='viwec-mobile-50' style='font-family: {$font_family};{$el_style}{$right_style}'><p style='{$p_style_value}'>{$unpaid_amount}</p></td>";
                $html    .= "</tr>";
            }
        }
        $email_render->table( $html );
    }
    
    public function add_installment_plan_to_email( $order ) {
        if ( $order ) {
            $is_deposit_order = $order->get_meta( '_depart_is_deposit_order' );
            if ( $is_deposit_order ) {
                $schedule = depart_get_schedule_payments_summary( $order );
                wc_get_template( 'emails/email-schedule-payments-summary.php', [ 'schedule' => $schedule ], '', DEPART_CONST['plugin_dir'] . '/templates/' );
            }
        }
    }
    
